static bool opt_int_not_pair(s7_scheme *sc, s7_pointer car_x)
{
  opt_info *opc;
  s7_pointer p;
  if (is_t_integer(car_x))
    {
      opc = alloc_opt_info(sc);
      opc->v[1].i = integer(car_x);
      opc->v[0].fi = opt_i_c;
      return_true(sc, car_x);
    }
  p = opt_integer_symbol(sc, car_x);
  if (!p)
    return_false(sc, car_x);
  opc = alloc_opt_info(sc);
  opc->v[1].p = p;
  opc->v[0].fi = opt_i_s;
  return_true(sc, car_x);
}

