	  opc->v[3].d_pd_f = func;
	  opc->v[1].p = lookup_slot_from(cadr(car_x), sc->curlet);
	  if (!is_slot(opc->v[1].p))
	    return_false(sc, car_x);
	  p = opt_float_symbol(sc, arg2);
