static bool opt_b_pp_ffo_is_eq(opt_info *o)
{
  s7_pointer b1 = o->v[4].p_p_f(o->sc, slot_value(o->v[1].p));
  s7_pointer b2 = o->v[5].p_p_f(o->sc, slot_value(o->v[2].p));
  return((b1 == b2) || ((is_unspecified(b1)) && (is_unspecified(b2))));
}

