	case OP_DO_STEP2: if (op_do_step2(sc)) goto DO_END; goto EVAL;

	DO_END_CLAUSES:
	  if (do_end_clauses(sc)) continue;
	DO_END_CODE:
	  {
	    goto_t next = do_end_code(sc);
	    if (next == goto_eval) goto EVAL;
	    if (next == goto_start) continue;
	    goto FEED_TO;
	  }


