	case OP_SET_S_S:        op_set_s_s(sc);  continue;
	case OP_SET_S_A:        op_set_s_a(sc);  continue;
	case OP_SET_S_P:        op_set_s_p(sc);  goto EVAL;
	case OP_SET_CONS:       op_set_cons(sc); continue;
 	case OP_SET_SAFE:	op_set_safe(sc); continue;

